const MemberCard = ({ image, onImageUpload }) => {
    return (
      <div className="relative w-100 h- bg-white rounded-md flex items-center justify-center shadow-md overflow-hidden">
        {image ? (
          <img src={image} alt="Member" className="w-full h-full object-cover" />
        ) : (
          <div className="text-gray-400 text-2xl font-bold">+</div>
        )}
        <input
          type="file"
          accept="image/*"
          className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
          onChange={onImageUpload}
        />
      </div>
    );
  };
  
  export default MemberCard;
