import React, { useState } from "react";
import MemberCard from "./MemberCard.js";

const MembersCards = () => {
  // Initialize with default images from the public folder
  const [images, setImages] = useState([
    "/default1.jpg", // Replace with your image names in the public folder
    "/default2.jpg",
    "/default3.jpg",
    "/default4.jpg",
  ]);

  const handleImageUpload = (index, event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        const newImages = [...images];
        newImages[index] = reader.result; // Replace the current image with the uploaded one
        setImages(newImages);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="bg-[#0f294c] p-8 rounded-lg h-[20vw] w-screen">
      <h2 className="text-white mb-4 text-[60px]">Members</h2>
      <div className="flex items-center gap-4 h-[80px]">
        {images.map((image, index) => (
          <MemberCard
            key={index}
            image={image}
            onImageUpload={(event) => handleImageUpload(index, event)}
          />
        ))}
        <button className="text-white font-bold text-4xl p-4">
          <i className="fa-solid fa-arrow-right"></i>
        </button>
      </div>
    </div>
  );
};

export default MembersCards;
