import React, { useState } from "react";

const MembersCarousel = () => {
  const [images, setImages] = useState(Array(4).fill(null)); // Initialize with placeholders

  const handleImageUpload = (index, event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        const newImages = [...images];
        newImages[index] = reader.result;
        setImages(newImages);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="bg-[#0f294c] p-6 rounded-lg w-fit-content">
      <h2 className="text-white text-xl mb-4">Members</h2>
      <div className="flex items-center gap-4">
        {images.map((image, index) => (
          <MemberCard
            key={index}
            image={image}
            onImageUpload={(event) => handleImageUpload(index, event)}
          />
        ))}
        <button className="text-white text-2xl font-bold">{" > "}</button>
      </div>
    </div>
  );
};

export default MembersCarousel;
